/**
 * ensure use on requestAnimationFrame, no matter how many components
 * on the page are using this mixin
 *
 * @class RAFAdmin
 * @public
 */
export default class RAFAdmin {
    private pool;
    constructor();
    /**
     * @method flush
     * @public
     */
    flush(): void;
    /**
     * @method add
     * @public
     */
    add(elementId: string | number | symbol, fn: Function): Function;
    /**
     * @method remove
     * @public
     */
    remove(elementId: string): void;
    /**
     * @method reset
     * @public
     */
    reset(): void;
}
